/// <reference path='../jasmine.d.ts' />
import longestPalindrome from "./q5-longest-palindromic-substring";

describe('Longest palindromic substring solution', () => {
    it('should be able to find right answser', () => {
        let s= 'a';
        let expectedResult = 'a';
        let actualResult = longestPalindrome(s);
        expect(actualResult).toEqual(expectedResult);
        s= '';
        expectedResult = 'lbabl';
        actualResult = longestPalindrome(s);
        expect(actualResult).toEqual(expectedResult);
    });
});
